import '../../App.css';
import React, { Component } from 'react';
import { ReactSVG } from 'react-svg';
import appliances from '../../svg/mainMenu/menuAppliances.svg';
import schedule from '../../svg/mainMenu/menuSchedule.svg';
import cancel from '../../svg/mainMenu/menuCancel.svg';
import energy from '../../svg/mainMenu/menuEnergy.svg';

class Menu extends Component {
  // handle svg file with arrows
  constructor(props) {
    super(props)
    this.handleChange = this.handleChange.bind(this)
    this.state = { result: [] }
  }
  handleKeyDown(e) {
    const { cursor, result } = this.state
    // arrow up/down button should select next/previous list element
    if (e.keyCode === 38 && cursor > 0) {
      this.setState( prevState => ({
        cursor: prevState.cursor - 1
      }))
    } else if (e.keyCode === 40 && cursor < result.length - 1) {
      this.setState( prevState => ({
        cursor: prevState.cursor + 1
      }))
    }
  }
  render(){
    return (
      <div className="equi">
          <ReactSVG
          afterInjection={(error, svg) => {
            if (error) {
              console.error(error)
              return
            }
            console.log(svg)
          }}
          beforeInjection={(svg) => {
            svg.classList.add('svg-class-name')
            svg.setAttribute('style', 'width: 950')
          }}
          className="background"
          evalScripts="always"
          fallback={() => <span>Error!</span>}
          loading={() => <span>Loading</span>}
          renumerateIRIElements={false}
          src={energy}
          useRequestCache={false}
          wrapper="div"/>
      </div>
    );
  }
}

export default Menu;
